    private void txtcalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtcalcularActionPerformed
        double unidades;
        double precio;
        double totalapagar;
        double cant_iva;
        double cant_des;
        double total_sin_iva;
        
        unidades=Double.parseDouble(txtunidades.getText());
        precio=Double.parseDouble(txtprecio.getText());
        
        total_sin_iva=precio*unidades;
        cant_iva=total_sin_iva*iva/100;
        cant_des=total_sin_iva*descuento/100;
        totalapagar=total_sin_iva+cant_iva-cant_des;
        txttotal.setText(" "+totalapagar);
    }//GEN-LAST:event_txtcalcularActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        iva=Double.parseDouble(txtiva.getText());
        descuento=Double.parseDouble(txtdescuento.getText());
    }//GEN-LAST:event_jButton1ActionPerformed

    private void txtconfiguracionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtconfiguracionActionPerformed
        dialogoconfiguracion.setSize(250,200);
        dialogoconfiguracion.setLocation(100,100);
        dialogoconfiguracion.setVisible(true);
    }//GEN-LAST:event_txtconfiguracionActionPerformed

